 const CeSeraitVraimentBienSi = "{\"parsed\":{\"_path\":\"/blog/ce-serait-vraiment-bien-si\",\"_dir\":\"blog\",\"_draft\":false,\"_partial\":false,\"_locale\":\"\",\"title\":\"Ce serait vraiment bien si…\",\"description\":\"\",\"permalink\":\"ce-serait-vraiment-bien-si\",\"date\":\"2024-06-24T23:20:25.334Z\",\"updatedAt\":\"2024-06-24T23:20:25.334Z\",\"layout\":\"post\",\"categories\":[\"mind dump\",\"business\"],\"draft\":true,\"body\":{\"type\":\"root\",\"children\":[{\"type\":\"element\",\"tag\":\"blockquote\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"J'ai écrit cet article en 2019, je ne sais même plus dans quel contexte… mais c'est hélas encore d'actualité en vrai. Je suis frustré au quotidien par l'absence de choix, le manque de logique ou le piètre service de certains business pourtant incontournable, pignon sur rue ce que tu veux… bref on est en 2024 maintenant, bonne lecture !\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le succès de la majorité des \\\"startups\\\" que vous connaissez réside dans le fait d'écouter ses utilisateurs/clients afin d'affiner le produit/service proposé initialement.\\n(pas uniquement, bien sûr, raccourci volontaire)\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Itérer et continuer d'innover…\\nC'est un peu de la co-création. La communauté une fois fédérée, ET écoutée sculpte le produit/service au gré de leurs désirs.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"On est d'accord ? Jusque la tout va bien ?\\nZoomons un peu, encore un peu plus, voila vous êtes bien centrés sur nos petites, toutes petites, îles ?\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Communautés, on connait et ce avant même que nos routes ne soient bitumées n'est-ce pas ?\\nEntreprises, ce n'est pas ce qui manque…\\nL'argent circule malgré tout ce que vous pouvez penser.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Pourquoi, NOS entreprises ne vont-elles pas dans le sens des personnes qui génèrent leur chiffre d'affaire ?\\nÉvidemment, ces personnes ne sont pas les cadres, commerciaux ou autres décisionnaires.\\nNon, je parle des personnes qui mettent effectivement la main à la poche, clients, utilisateurs, abonnés.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La vous me répondrez qu'il y a des boîtes à idée à l'entrée et parfois même des petits papiers à côté, peut-être même un stylo qui fonctionne.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Nous sommes en 2019. Oui, je vous le confirme.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Et je pense qu'il existe suffisamment de moyen de poser les bonnes questions, repérer une tendance et y répondre, essayer, constater les résultats, conserver ou revenir sur ses pas…\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La majorité d'entre vous n'ont souvent même pas besoin de poser des questions… juste ouvrir les oreilles, yeux, LIRE SES EMAILS, chercher à comprendre les commentaires et les réactions sur les réseaux sociaux.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Bref, je ne suis en aucun cas un communiquant ou professionnel en marketing… J'ai même pas de SIRET (ahah)\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Pas besoin d'acheter une solution d'ERP/CRM/SAP à 30k€ pour innover.\\nPas besoin d'être une startup pour écouter ses clients et améliorer/changer son modèle économique, son produit.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Pas besoin de consultant en transformation digitale, vous avez surement des employés qui ont des avis et des idées à tester, contre un peu de reconnaissance.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Trop long ? Surement, heureusement je n'écris pas pour être lu… allez salut \"},{\"type\":\"element\",\"tag\":\"d\",\"props\":{},\"children\":[]}]}],\"toc\":{\"title\":\"\",\"searchDepth\":2,\"depth\":2,\"links\":[]}},\"_type\":\"markdown\",\"_id\":\"content:blog:Ce-serait-vraiment-bien-si.md\",\"_source\":\"content\",\"_file\":\"blog/Ce-serait-vraiment-bien-si.md\",\"_stem\":\"blog/Ce-serait-vraiment-bien-si\",\"_extension\":\"md\"},\"hash\":\"9ho3rDShKQ\"}";

export { CeSeraitVraimentBienSi as default };
